    private void btn_cadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_cadastroActionPerformed
        this.setVisible(false);
        CadastrarFuncionarios cadastrarFuncionario = new CadastrarFuncionarios();
        cadastrarFuncionario.setVisible(true);
    }//GEN-LAST:event_btn_cadastroActionPerformed

    private void btn_logarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_logarActionPerformed
        try {
            FuncionarioRepository f = new FuncionarioRepository();
            FuncionarioModel funcionario = new FuncionarioModel();
            String cpf = txt_cpf.getText().replace(".", "");
            funcionario.setCpf(cpf);
            funcionario.setSenha(txt_senha.getText());
            f.Logar(funcionario, this);
        } catch (SQLException ex) {
            Logger.getLogger(LoginView.class.getName()).log(Level.SEVERE, null, ex);
        } catch (ClassNotFoundException ex) {
            Logger.getLogger(LoginView.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btn_logarActionPerformed

    private void txt_cpfKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_cpfKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            txt_senha.requestFocus();
        }
    }//GEN-LAST:event_txt_cpfKeyPressed

    private void txt_senhaKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txt_senhaKeyPressed
        if (evt.getKeyCode() == evt.VK_ENTER) {
            btn_logar.requestFocus();

        }
    }//GEN-LAST:event_txt_senhaKeyPressed

